const s = {
  attack: 'Нападение',
  defense: 'Защита',
  luck: 'Удача',
  leadership: 'Лидерство',
  enlightenment: 'Образование',
  dark: 'Магия Тьмы',
  destructive: 'Магия Хаоса',
  light: 'Магия Света',
  summon: 'Магия Природы',
  sorcery: 'Чародейство',
  special: 'Фракция'
}

function parse(text) {
  text = text.match(/;builds=(.+)$/)[1];

  let rows = text.split('$');
  let list = [];

  for (let r of rows) {
    let row = r.split('|');
    if (row.length != 10) continue;

    let section = 'special';
    let id = row[0];
    let name = row[2];
    let main = false;

    if (id.match(/\d$/)) {
      section = id.slice(0, -1)
      main = true;

    } else {
      for (let j = 0; j < 4; j++) {
        let v = row[4 + j];
        if (v && v !== 'none') {
          if (v.match(/\d$/)) {
            section = v.slice(0, -1);
            break;
          }
        }
      }
    }

    list.push({
      id,
      name,
      section,
      main
    })
  }

  return list;
}

function format(...lists) {
  let map = {};

  for (let list of lists) {
    for (let { id, name, section, main } of list) {
      if (!map[section]) {
        map[section] = { 
          id: section,
          name: s[section],
          main: {}, 
          other: {}
        };
      }
      map[section][ main ? 'main' : 'other' ][id] = { id, name };
    }
  }

  let table = [];
  for (let sectionId of Object.keys(s)) {
    let section = map[sectionId];
    let item = {
      id: section.id,
      name: section.name,
      list: Object.keys(section.main).map((key) => {
        let { id, name } = section.main[key];
        return { id, name, main: true }
      })
    }
    let other = Object.keys(section.other);
    other.sort((a,b) => {
      a = section.other[a].name;
      b = section.other[b].name;

      return (a > b) 
        ? 1 
        : (a < b) 
          ? -1 
          : 0
    });
    item.list.push(
      ...other.map((id) => section.other[id])
    );
    table.push(item);
  }

  return table;
}


function print(table) {
  const
    write = process.stdout.write.bind(process.stdout);

  write(`[`);
  for (let { id, name, list } of table) {
    write(`{ id: "${id}", name: "${name}", list: [\n`);
    for (let { id, name, main } of list) {
      write(`    { id: "${id}", name: "${name}"`);
      if (main) {
        write(`, main: true`);
      }
      write(` },\n`);
    }
    write(`  ]}, `);
  }
  write(`]\n`);
}


print(format(
  ...Object.keys(d).map((k) => parse(d[k]))
));



